        el += '<section>';
        el += '<div class="header">';
        el += sec.title;
        el += '</div>';
        el += layoutDocTree(ctx, sec.pages);
        el += '</section>';
      });
      el += '</div>';
    } else {
      el += '<div class="body fs14">';
      el += '<div class="body">';
      el += layoutDocTree(ctx, proj.pages);
      el += '</div>';
      el += '</div>';
    }
  }
  el += '</details>';
  // end
  el += '</div>';
  return el;
};
