/**
 * copy.js v2 | https://github.com/chiyuki0325/hexo-theme-stellaris/
 * 格式与官方标签插件一致使用空格分隔，中括号内的是可选参数（中括号不需要写出来）
 *
 * {% copy xxx %}
 * {% copy git xaoxuu/hexo-theme-stellar %}
 *
 */

'use strict';

let copy_index = 0;
module.exports = ctx => function (args) {
  args = ctx.args.map(args, ['width', 'git'], ['text']);
  if (args == undefined || args.text == undefined) {
    return '';
  }
  var text = args.text;
  if (args.git) {
    if (text.substr(0, 1) == '/') {
      text = text.substring(1);
    }
    if (args.git == 'ssh') {
      text = 'git@github.com:' + text + '.git';
    } else if (args.git == 'gh') {
      text = 'gh repo clone ' + text;
    } else {
      text = 'https://github.com/' + text + '.git';
    }
  }
  const copy_id = 'copy_' + ++copy_index;
  var el = '';
  el += '<div class="tag-plugin copy"';
  if (args.width) {
    el += ' width="' + args.width + '"';
  }
  el += '>';
  el += '<input class="copy-area" readonly id="' + copy_id + '"';
