    el += loadIcon() + loadLink();
    el += '</div>';
    // bottom
    el += '<div class="bottom">';
    el += loadTitle() + loadDesc();
    el += '</div>';
  } else {
    // left
    el += '<div class="left">';
    el += loadTitle() + loadLink();
    el += '</div>';
    // right
    el += '<div class="right">';
    el += loadIcon();
    el += '</div>';
  }

  // end
  el += '</a></div>';
  return el;
};
