  const {
    mathjax
  } = plugins;
  const {
    enabled,
    cdn
  } = mathjax;
  if (enabled) {
    const loadMathScript = `
            function loadMathJax() {    
                if (typeof MathJax == "undefined")  // 没有载入脚本就先载入脚本
                    stellar.loadScript('${cdn}', {defer:true});
                else // 否则立即渲染
                    MathJax.typeset();
            }
            InstantClick.on('change', () => {
